"use strict";(self.webpackChunkgrc=self.webpackChunkgrc||[]).push([[17708],{18048:e=>{e.exports=JSON.parse('{"abstract":"Symmetric Toeplitz tridiagonal systems arise in many scientific applications.\\nAn efficient algorithm, the Simple Parallel Prefix (SPP) algorithm, was previously\\nproposed for solving symmetric Toeplitz tridiagonal systems on SIMD and vector\\ncomputers. Based on the SPP algorithm, a scalable parallel algorithm is proposed for\\nsolving periodic symmetric Toeplitz tridiagonal systems in this study. The newly\\nproposed algorithm has the same parallel computation count as that of the SPP algorithm\\nfor non-periodic systems, and it requires only shift communication.","authors":["X.-H. Sun"],"date":"January, 2001","links":{"pdf":"http://cs.iit.edu/~scs/assets/files/scala01.pdf"},"month":1,"slug":"sun-2001-scalable-parallel-bb68","tags":[],"title":"A Scalable Parallel Algorithm for Periodic Symmetric Toeplitz Tridiagonal Systems","type":"Journal","venue":"International Journal of Computer Research, Vol. 10, No. 1, 2001, pp. 89-98.","year":2001}')}}]);