"use strict";(self.webpackChunkgrc=self.webpackChunkgrc||[]).push([[51077],{75127:e=>{e.exports=JSON.parse('{"abstract":"In this study, the authors propose a simple performance model to\\npromote a better integration between the parallel I/O middleware\\nlayer and parallel file systems. They show that application-\\nspecific data layout optimization can improve overall data access\\ndelay considerably for many applications. Implementation results\\nunder MPI-IO middleware and PVFS2 file system confirm the\\ncorrectness and effectiveness of their approach, and demonstrate\\nthe potential of data layout optimization in petascale data storage.","authors":["X.-H. Sun","Y. Chen","Y. Yin"],"date":"November, 2009","doi":"10.1145/1713072.1713077","links":{"pdf":"http://cs.iit.edu/~scs/assets/files/pdsw09.pdf"},"month":11,"slug":"sun-2009-data-layout-d61b","tags":[],"title":"Data Layout Optimization for Petascale File Systems","type":"Workshop","venue":"The 4th Petascale Data Storage Workshop (in conjunction with ACM/IEEE SC\'09), Portland, OR, USA","year":2009}')}}]);