"use strict";(self.webpackChunkgrc=self.webpackChunkgrc||[]).push([[59797],{55308:e=>{e.exports=JSON.parse('{"abstract":"Computer networks exist to provide services to users. In\\ndomains where more then one network dominates and provides\\nuseful services, users will want to avail themselves of services on\\neither of the networks. This paper looks at the convergence of two\\nnetworks in the telecommunication domain: the Internet and the\\nPublic Switched Telephone Network and discusses techniques to\\naccess services between the two networks, including an\\narchitecture for realizing services which would not be possible if\\neither of the networks was operating in isolation.","authors":["V. K. Gurbani","X.-H. Sun"],"date":"May, 2003","doi":"10.1109/icc.2003.1204427","links":{"pdf":"http://cs.iit.edu/~scs/assets/files/GuSuIcc03.pdf"},"month":5,"slug":"gurbani-2003-services-spanning-75a3","tags":[],"title":"Services spanning heterogeneous networks","type":"Conference","venue":"The 2003 IEEE International Conference on Communications (ICC 2003), Anchorage, Alaska","year":2003}')}}]);