"use strict";(self.webpackChunkgrc=self.webpackChunkgrc||[]).push([[73167],{22962:e=>{e.exports=JSON.parse('{"advisor":"Dr. Xian-He Sun","bio":"Jie Ye is a PhD candidate in the Department of Computer Science at the Illinois Institute of Technology and a member of the Gnosis Research Center. She is advised by Dr. Xian-He Sun and Dr. Anthony Kougkas. Her research focuses on accelerating LLM/DNN inference; KV cache management and offloading of models and KV caches; advanced data-transfer strategies between training applications and inference systems; optimizing LLM/DNN training; and GPUDirect data transfer.","image":"ye.png","links":{"email":"jye20@hawk.illinoistech.edu","github":"https://github.com/Jye-525","linkedin":"https://www.linkedin.com/in/jie-ye-275b08252/","orcid":"https://orcid.org/0009-0003-3985-7896"},"name":"Jie Ye","researchInterests":["Distributed Storage","Parallel and Distributed Systems","LLM/DNN Inference Optimization","LLM KV Cache management","LLM/DNN Training Optimization"],"slug":"jie-ye","title":"PhD Candidate","type":"researcher"}')}}]);